mapper.insert:
INSERT INTO comment (
    post_no
    , created_at
    , author_no
    , content
    , status
) VALUES (
    #{postNo}
    , UNIX_TIMESTAMP()
    , #{authorNo}
    , #{content}
    , 'POSTED'
)

mapper.select:
SELECT COUNT(*) FROM comment c INNER JOIN member m ON c.author_no = m.no WHERE c.status = 'POSTED'
AND c.author_no = #{memberNo}
AND c.post_no = #{postNo}
AND ( c.content LIKE CONCAT('%', #{pagingRequestDTO.searchKeyword}, '%') OR m.name LIKE CONCAT('%', #{pagingRequestDTO.searchKeyword}, '%') )

mapper.update:
UPDATE
    comment c
SET
    c.content = #{content}
    , c.modified_at = UNIX_TIMESTAMP()
    , c.modifier_no = #{modifierNo}
WHERE
    c.no = #{commentNo}
    AND c.status != 'DELETED'
