mapper.insert:
INSERT INTO file_info (
    origin_name
    , save_name
    , save_path
    , extension
    , size
    , created_at
    , author_no
    , modified_at
    , modifier_no
    , parent_no
    , parent_type
    , status
) VALUES (
    #{originName} 
    , #{saveName} 
    , #{savePath} 
    , #{extension}
    , #{size} 
    , UNIX_TIMESTAMP()
    , #{authorNo}
    , NULL
    , NULL
    , #{parentNo}
    , #{parentType}
    , #{status}
);

mapper.update:
UPDATE
    file_info f
SET
    f.status = 'NORMAL'
    , f.save_path = #{newPath}
    , f.parent_no = #{parentNo}
    , f.parent_type = #{fileInfoParentType}
WHERE
    f.no = #{fileInfoNo}
