mapper.insert:
INSERT INTO member (
    name
    , id
    , pw
    , email
    , phone
    , status
) VALUES (
    #{name}
    , #{id} 
    , #{pw} 
    , #{email} 
    , #{phone} 
    , #{status}
);

mapper.select:
SELECT 

FROM 
    member m

    m.status = 'NORMAL'

        AND m.id = #{id}


        AND m.email = #{email}

mapper.insert:
INSERT INTO member_auth (
