mapper.insert:
INSERT INTO post (
    title
    , content
    , created_at
    , author_no
    , status
) VALUES (
    #{title}
    , #{content}
    , UNIX_TIMESTAMP()
    , #{authorNo}
    , #{status}
)

mapper.update:
UPDATE
    post p
SET
    p.title = #{title}
    , p.content = #{content}
    , p.modified_at = UNIX_TIMESTAMP()
    , p.modifier_no = #{modifierNo}
WHERE
    p.no = #{postNo}
