mapper.insert:
INSERT INTO post (
    title
    , content
    , created_at
    , author_no
    , status
) VALUES (
    #{title}
    , #{content}
    , UNIX_TIMESTAMP()
    , #{authorNo}
    , #{status}
)

mapper.update:
UPDATE
    post p
SET
    p.title = #{title}
    , p.content = #{content}
    , p.modified_at = UNIX_TIMESTAMP()
    , p.modifier_no = #{modifierNo}
WHERE
    p.no = #{postNo}

mapper.select:
SELECT p.no , p.title , m.name AS author_name , p.created_at FROM post p INNER JOIN member m ON p.author_no = m.no WHERE p.deleted_at IS NULL
AND p.title LIKE CONCAT('%', #{searchDTO.titleKeyword}, '%')
AND m.name LIKE CONCAT('%', #{searchDTO.authorNameKeyword}, '%')
AND p.author_no = #{authorNo}
ORDER BY p.no DESC LIMIT #{pagingDTO.limit} OFFSET #{pagingDTO.offset}
